package main

import (
		 "net/http"
		 "time"
		 "fmt"  )

const base64GifPixel = ""

func main() {

	duration, _ := time.ParseDuration("1000ns")

	server := &http.Server{
			Addr       : "172.22.51.166:8082",
			IdleTimeout: duration,
		//	Handler    : 
		}

	http.HandleFunc("/", getINFO)

	server.ListenAndServe()
}

func getINFO(w http.ResponseWriter, r *http.Request) {
    fmt.Fprintf(w, r.Method + " " + r.Host + r.URL.Path)
}